sec.preformat:
#@int (label=“channel to analyze”)ch
#@int (label=“maximum object radius (in pixel)”)R
#@int (label=“minimum object area (in pixel)”)minArea
#@boolean (label=“use automatic threshold”)isAutoThresh

#@float (label=“manual threshold”)T
#@boolean (label=“use watershed”)isWatershed
# Author: Benoit Lombardot, Scientific Computing Facility, MPI-CBG
# 2014-06-26 first version for Jaroslav Icha (Norden Lab)
# 2016-07-19 simplify parameter input and update comments for publication (for Jaroslav Icha (Norden Lab))

# The code was tested with Fiji 2.0.0 rc 49 / 1.51d on July 19th 2016

from ij import IJ;
from ij.gui import Roi;
from ij.plugin import Duplicator;
from ij.plugin import ZProjector as Zproj
from ij.process import ImageStatistics;
from ij.gui import ShapeRoi;
from ij import ImagePlus;
from ij.process import ByteProcessor;
from ij.measure import ResultsTable;
from ij import WindowManager;
from ij.gui import Overlay;
from ij import WindowManager;
from ij.text import TextWindow;

from java.awt import Color;

from util import FindConnectedRegions;

from mcib3d.image3d import ImageHandler;
from mcib3d.image3d.processing import FastFilters3D;
from mcib3d.image3d.regionGrowing import Watershed3D;

# grab visible image
imp0 = IJ.getImage();
stats_0 = imp0.getStatistics(ImageStatistics.AREA);
area_analyzed = stats_0.area;

# grab roi
roi = imp0.getRoi();
imp0.killRoi();

ratioXoverZ= imp0.getCalibration().pixelDepth/imp0.getCalibration().pixelWidth;
# duplicate the fluorescence channel
nZ = imp0.getNSlices();
nF = imp0.getNFrames();
imp = Duplicator().run(imp0, ch, ch, 1, nZ, 1, nF);

# max intensity projection zproj = Zproj(imp);
zproj.setMethod(Zproj.MAX_METHOD);
zproj.setStartSlice(1);
zproj.setStopSlice(nZ);
zproj.doProjection();
imp_proj = zproj.getProjection();

imp_proj_4disp = Duplicator().run(imp_proj);
# filter: gauss(1), bg removal, threshold (or maxima then watershed)
IJ.run(imp_proj, “Gaussian Blur…”, “sigma=1”);
IJ.run(imp_proj, “Subtract Background…”, “rolling=“+str(R) );

# set a threshold
if isAutoThresh:
         imp_proj.getProcessor().setAutoThreshold(“Otsu dark”);
         T = imp_proj.getProcessor().getMinThreshold();
else:
         stats = imp_proj.getStatistics(ImageStatistics.MIN_MAX);
         IJ.setThreshold(imp_proj,T,stats.max);
zproj2 = Zproj(imp);
zproj2.setMethod(Zproj.SUM_METHOD);
zproj2.setStartSlice(1);
zproj2.setStopSlice(nZ);
zproj2.doProjection();
imp_proj_4meas = zproj2.getProjection();

# performed image segmentation : output is a B&W image imp_proj
if isWatershed == True:
         img = ImageHandler.wrap(imp_proj);
         max_img = FastFilters3D.filterImage(img, FastFilters3D.MAXLOCAL, 3, 3, 3, 2, True);
         ws_img = Watershed3D(img, max_img, int(T), int(T) );
         label_imp = ImagePlus(“labelled peaks”,
ws_img.getWatershedImage3D().getImagePlus().getImageStack());
         # object are separated by pixel of value one. in 2d this work nicely with particle analyzer
         imp_proj = label_imp;
         stats = imp_proj.getStatistics(ImageStatistics.MIN_MAX);
         IJ.setTh reshold(imp_proj,2,stats.max);
else:
         IJ.run(imp_proj, “Convert to Mask”, “ “);

# Take the intersection of the manually inputed roi and detectected fluorescence spot
IJ.run(imp_proj, “Create Selection”, ““);
roi2 = imp_proj.getRoi();
imp_proj.killRoi()
if roi!=None:
         roi2.and(ShapeRoi(roi) );
imp_proj.setRoi(roi2);
IJ.run(imp_proj, “Create Mask”, “ “);
mask = IJ.getImage();

# analysis of the detected objects
imp_proj_4meas.setTitle(“imageToAnalyze”);
imp_proj_4meas.killRoi();
imp_proj_4meas.show();
IJ.run(“Set Measurements…”, “area mean redirect=“+ imp_proj_4meas.getTitle() +” decimal=3”);
IJ.run(mask, “Analyze Particles…”, “size=“+str(minArea)+”-Infinity display clear”);

imp_proj_4meas.hide();
imp_proj_4meas.flush();
mask.hide();
mask.flush();

# browse the value and count the object obove the threshold
rt = ResultsTable.getResultsTable();
frame = WindowManager.getFrame(“Results”);
if not frame==None:
         frame.close(False);
nval = rt.getCounter();
count = 0;
sumI = 0;
sumA = 0;
for i in range(nval):
         count = count+1;
         sumI = sumI + rt.getValue(“Mean”,i);
         sumA = sumA + rt.getValue(“Area”,i);

# display results
rt_summary = ResultsTable();
rt_summary.incrementCounter();
rt_summary.addValue(“count”,count);
rt_summary.addValue(“sum area”,sumA);
rt_summary.addValue(“sum intensity”,sumI);
rt_summary.addValue(“area analyzed”,area_analyzed);

#display result table
rt_summary.show(“image summary”);
# display overlay on the max projection
ov = Overlay();
roi2.setStrokeColor(Color. red);
ov.add(roi2);
if roi!=None:
         roi.setStrokeColor(Color.green);
         ov.add(roi);
         imp0.setRoi(roi);

imp_proj_4disp.setOverlay(ov);
imp_proj_4disp.setTitle(“detection overlay on maxima projection”)
imp_proj_4disp.show();
IJ.run(imp_proj_4disp, “Enhance Contrast”, “saturated=0.5”);
