sec.preformat:
using ODE,StatsBase,MAT,HDF5,JLD
# code for WSP differential equations
function lindodeWSP(t,y)
   # convert y to reactants for ease of reading ABD=y[1];
   ABDm=y[2];
   ABDmp=y[3];
   Ep=y[4];
   Rp=y[5];
   E=y[6];
   F=y[7];
   Fp=y[8];
   R=y[9];
   # pull reaction rates from rs variable
   r1=rs[1];r2=rs[2];r3=rs[3];r4=rs[4];r5=rs[5];r6=rs[6];
   # compute derivatives, i.e. y’
   yp=zeros(size(y));
   yp[1]=r2*Fp*ABDm-r1*ABD*Cm; # dABD/dt
   yp[2]=r1*ABD*Cm-r2*Fp*ABDm-r3*S*ABDm+r4*E*ABDmp; # dABDm/dt
   yp[3]=−r4*E*ABDmp + r3*S*ABDm; # dABDmp/dt
   yp[4]=r4*E*ABDmp - r5*Ep*R -r6*Ep*F; # dEp/dt yp[5]=r5*R*Ep; # dRp/dt
   yp[6]=r6*Ep*F-r4*E*ABDmp+r5*R*Ep; # dE/dt
   yp[7]=−r6*Ep*F +r2*Fp*ABDm; # dF/dt yp[8]=r6*Ep*F -r2*Fp*ABDm; # dFp/dt yp[9]=−r5*R*Ep-.01*R; # dR/dt
   return yp
end
# code for AWS differential equations
function lindodeAWS(t,y)
   # convert y to reactants for ease of reading X=y[1];
   XR=y[2];
   O=y[3];
   Op=y[4];
   OX=y[5];
   R=y[6];
   RR=y[7];
   # pull reaction rates from rs variable r1=rs[1];r2=rs[2];r3=rs[3];r4=rs[4];
   # compute derivatives, i.e. y’ yp=zeros(size(y));
   yp[1]=−r2*X*Op-r3*X*R; # dX/dt
   yp[2]=r3*X*R; # dXR/dt
   yp[3]=−r1*S*O; # dO/dt
   yp[4]=r1*S*O-r2*X*Op; # dOp/dt
   yp[5]=r2*Op*X; # dOX/dt
   yp[6]=−r3*X*R-r4*R*R; # dR/dt
   yp[7]=r4*R*R; # dRR/dt
   return yp;
end
# code for MWS differential equations
function lindodeMWS(t,y)
   # convert y to reactants for ease of reading
   G=y[1];
   Gp=y[2];
   E=y[3];
   GE=y[4];
   C=y[5];
   GC=y[6];
   # pull reaction rates from rs variable
   r1=rs[1];r2=rs[2];r3=rs[3];
   # compute derivatives, i.e. y’
   yp=zeros(size(y));
   yp[1]=−r1*G*S; # dG/dt
   yp[2]=r1*G*S-r2*Gp*E-r3*Gp*C;# dGp/dt
   yp[3]– -r2*Gp*E;# dE/dt
   yp[4]– r2*Gp*E;# dGE/dt
   yp[5]=−r3*Gp*C;# dC/dt
   yp[6]=r3*Gp*C; # dGC/dt return yp;
end

% create record of how parameters change down, none, up for WSP rs1=rand(1,6);
totnumruns=3.^length(rs1);
paramsWSP=zeros(totnumruns,length(rs1));
v=zeros(size(rs1));
i0=1;
while i0<=totnumruns;
  num=i0-1;
  for i1=length(rs1)−1:−1:0;
       v(i1+1)=floor(num/(3^i1));
       num=num-v(i1+1)*3^i1;
  end
  v=v+1;
  paramsWSP(i0,:)=v;
  i0=i0+1;
end
% create record of how parameters change down, none, up for MWS rs1=rand(1,3);
totnumruns=3.^length(rs1); paramsMWS=zeros(totnumruns,length(rs1)); v=zeros(size(rs1));
i0=1;
while i0<=totnumruns;
   num=i0-1;
   for i1=length(rs1)−1:−1:0;
      v(i1+1)=floor(num/(3^i1));
      num=num-v(i1+1)*3^i1;
 end
 v=v+1;
 paramsMWS(i0,:)=v;
 i0=i0+1;
end
% Load data
load pathway results complete MWS.mat resMWS=res;
yorigMWS=yorig’; clear res yorig
load pathway results complete WSP.mat resWSP=res;
yorigWSP=yorig’;
% Variables and data storage to compare likelihood of pathways numsampWSP=size(resWSP,2); % in case want to use fewer samples numsampMWS=size(resMWS,2); % in case want to use fewer samples
perange=10.^[−7:.5:−1]; % range for probability enabling mutations pdrange=10.^[−7:.5:−1]; % range for probability disabling mutations
pemat=zeros(length(perange),length(pdrange)); % matrix for plotting data and reference pdmat=zeros(size(pemat)); % matrix for plotting data and reference psummatWSP=zeros(size(pemat)); % matrix for probability WSP used psummatMWS=zeros(size(pemat)); % matrix for probability MWS used
tol=0;
% Code to compare likelihood of pathways for i0=1:length(perange)
   for j0=1:length(pdrange)
   % Retrieve probabilities pe=perange(i0); pd=pdrange(j0); pemat(i0,j0)=pe;
   pdmat(i0,j0)=pd;
   % WSP computation
   pmatforr=ones(6,1)*[pd 1-pe-pd pe];
   psum=0; % total sum of (prob of rxn changes) X (number of times WS produced)
for i1=1:size(resWSP,1)
probevent=1; % initialize, probability to get combination of down, none, up for rxns for j1=1:6;
probevent=probevent*pmatforr(j1,paramsWSP(i1,j1)); % multiply by prob of each change
    end
psum=psum+probevent*sum(resWSP(i1,1:numsampWSP)>yorigWSP(1:numsampWSP)+tol)/numsampWSP;
end
psummatWSP(i0,j0)=psum;
% MWS computation pmatforr=ones(3,1)*[pd 1-pe-pd pe];
psum=0; % total sum of (prob of rxn changes) X (number of times WS produced)
for i1=1:size(resMWS,1)
    probevent=1; % initialize, probability to get combination of down, none, up for rxns for j1=1:3;
    probevent=probevent*pmatforr(j1,paramsMWS(i1,j1)); % multiply by prob of each change
    end
    end psum=psum+probevent*sum(resMWS(i1,1:numsampMWS)>yorigMWS(1:numsampMWS)+tol)/numsampMWS;
    end
end
psummatMWS(i0,j0)=psum;
% Plot data close all figure
contourf(pemat,pdmat,log2(psummatWSP./psummatMWS),400,‘LineStyle’,‘None’) set(gca,‘xScale’,‘log’,‘yScale’,‘log’,‘TickLength’,[.025 .025],‘LineWidth’,3);
set(gca,‘FontSize’,18,‘xTick’,10.^[−7:1:−1],‘yTick’,10.^[−7:1:−1]); xlabel(‘Probability of enabling change’,‘FontSize’,24); ylabel(‘Probability of disabling change’,‘FontSize’,24); c=colorbar(‘FontSize’,18);
c.Label.String=‘log 2 ratio probability WSP/MWS’; colormap jet;
axis square
eval([‘print -f1 -depsc -r300 WSP vs MWS contour.eps’]);

% create record of how parameters change down, none, up for WSP rs1=rand(1,6);
totnumruns=3.^length(rs1); paramsWSP=zeros(totnumruns,length(rs1)); v=zeros(size(rs1));
i0=1;
  while i0<=totnumruns;
    num=i0-1;
    for i1=length(rs1)−1:−1:0;
       v(i1+1)=floor(num/(3^i1));
end v=v+1;
          num=num-v(i1+1)*3^i1;
          paramsWSP(i0,:)=v;
          i0=i0+1;
end
% create record of how parameters change down, none, up for AWS
 rs1=rand(1,4);
totnumruns=3.^length(rs1); paramsAWS=zeros(totnumruns,length(rs1)); v=zeros(size(rs1));
i0=1;
while i0<=totnumruns;
   num=i0-1;
   for i1=length(rs1)−1:−1:0;
       v(i1+1)=floor(num/(3^i1));
       num=num-v(i1+1)*3^i1;
  end
  v=v+1;
  paramsAWS(i0,:)=v;
  i0=i0+1;
end
% Load data
load pathway results complete AWS.mat resAWS=res;
yorigAWS=yorig’; clear res yorig
load pathway results complete WSP.mat resWSP=res;
yorigWSP=yorig’;
% Variables and data storage to compare likelihood of pathways numsampWSP=size(resWSP,2); % in case want to use fewer samples numsampAWS=size(resAWS,2); % in case want to use fewer samples
perange=10.^[−7:.5:−1]; % range for probability enabling mutations pdrange=10.^[−7:.5:−1]; % range for probability disabling mutations
pemat=zeros(length(perange),length(pdrange)); % matrix for plotting data and reference pdmat=zeros(size(pemat)); % matrix for plotting data and reference psummatWSP=zeros(size(pemat)); % matrix for probability WSP used psummatAWS=zeros(size(pemat)); % matrix for probability MWS used
tol=0;
fac=5; % factor increase of mutation because of hotspot
% Code to compare likelihood of pathways
   for i0=1:length(perange)
      for j0=1:length(pdrange)
       % Retrieve probabilities
        pe=perange(i0); pd=pdrange(j0);
        pemat(i0,j0)=pe; pdmat(i0,j0)=pd;
        % WSP computation
        pmatforr=ones(6,1)*[pd 1-pe-pd pe];
        psum=0; % total sum of (prob of rxn changes) X (number of times WS produced)
        for i1=1:size(resWSP,1)
           probevent=1; % initialize, probability to get combination of down, none, up for rxns
end
for j1=1:6;
      probevent=probevent*pmatforr(j1,paramsWSP(i1,j1)); % multiply by prob of each change
          end
psum=psum+probevent*sum(resWSP(i1,1:numsampWSP)>yorigWSP(1:numsampWSP)+tol)/numsampWSP;
end
psummatWSP(i0,j0)=psum;
% AWS computation pmatforr=ones(4,1)*[pd 1-pe-pd pe];
pmatforr(4,:)=[.5*pd 1-.5*pd-.5*pe .5*pe]; % because only reactant in dimerization
pmatforr(3,:)=[fac*pd 1-fac*pd-fac*pe fac*pe]; % effect of hotspot pmatforr(2,:)=[fac*pd 1-fac*pd-fac*pe fac*pe]; % effect of hotspot
psum=0; % total sum of (prob of rxn changes) X (number of times WS produced)
for i1=1:size(resAWS,1)
probevent=1; % initialize, probability to get combination of down, none, up for rxns for j1=1:4;
    probevent=probevent*pmatforr(j1,paramsAWS(i1,j1)); % multiply by prob of each change
  end
 end psum=psum+probevent*sum(resAWS(i1,1:numsampAWS)>yorigAWS(1:numsampAWS)+tol)/numsampAWS;
end
end
psummatAWS(i0,j0)=psum;
% Plot data
close all
figure
contourf(pemat,pdmat,log2(psummatWSP./psummatAWS),400,‘LineStyle’,‘None’)
set(gca,‘xScale’,‘log’,‘yScale’,‘log’,‘TickLength’,[.025 .025],‘LineWidth’,3);
set(gca,‘FontSize’,18,‘xTick’,10.^[−7:1:−1],‘yTick’,10.^[−7:1:−1]);
xlabel(‘Probability of enabling change’,‘FontSize’,24);
ylabel(‘Probability of disabling change’,‘FontSize’,24);
c=colorbar(‘FontSize’,18);
c.Label.String=‘log 2 ratio probability WSP/AWS’;
colormap jet;
axis square
eval([‘print -f1 -depsc -r300 WSP vs AWS contour.eps’]);
